<?phpnamespace BackBundle\Controller;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\HttpFoundation\Response;use BackBundle\Entity\User;use Symfony\Component\Ldap\LdapClient;/** * Class UserController * @package BackBundle\Controller * @Route("/user") */class ComputerController extends Controller{        /**     * @Route("/list-computers.html", name="back_computer_list")     * @return RedirectResponse|Response     */    function listComputerAction()    {        $ad = $this->get("ldap_service");        $computers = $ad->getAllComputer();        return $this->render('BackBundle:Computer:list.html.twig', array(            "computers" => $computers,        ));    }}/* *  *  *         $data = null;        $OU = null;        $base = $this->container->getParameter('ldap_base_dn');        $ldap->bind($this->container->getParameter('ldap_search_dn'), $this->container->getParameter('ldap_pass'));        if ($ldap && $base) {            $query = "(&(objectClass=User)(objectClass=person)(!(objectClass=computer)))";            if ($OU == null) {                $tree = $base;            } else {                $tree = "OU=Users," . "OU=" . $OU . "," . $base;            }            $search = $ldap->find($base, $query);        }        dump($search); */