<?phpnamespace BackBundle\Controller;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\HttpFoundation\Response;use BackBundle\Entity\User;use Symfony\Component\Security\Core\Security;/** * Class UserController * @package BackBundle\Controller */class SecurityController extends Controller{    /**     * @Route("/login", name="login")     */    public function loginAction(Request $request)    {        $user = $this->getUser();        if ($user instanceof UserInterface) {            return $this->redirectToRoute('back_home');        }        $authenticationUtils = $this->get('security.authentication_utils');        $error = $authenticationUtils->getLastAuthenticationError();        $lastUsername = (null === $error) ? '':$authenticationUtils->getLastUsername();                return $this->render('BackBundle:Security:login.html.twig', array(                'last_username' => $lastUsername,                'error' => $error ? $error->getMessage() : NULL,            )        );    }    /**     * @Route("/logout", name="logout")     */    public function logoutAction(Request $request)    {        // Nothing needed.    }}